import PropTypes from 'prop-types';
import React, { Component } from 'react';

//Material UI Imports
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";
import Paper from "@material-ui/core/Paper";
import { withRouter } from 'react-router-dom';
import {Typography} from "@material-ui/core";
import BackButton from "../../BackButton";
import FilterIcon from '@material-ui/icons/FilterList';
import Popover from '@material-ui/core/Popover';
import SearchSelect from 'react-select';

class RegistrationLanding extends Component {
    constructor(props) {
        super(props);
        this.state = {
            courses:[],
            instructors:[],
            anchorEl:'',
            filter: {
                instructor: [],
                grade:[],
                subject:[],
            },
            mobileViewToggle: false,
        }
    }

    componentDidMount() {
        this.setState({
            courses: Object.keys(this.props.courses).filter(
                (courseID)=>{return this.props.courses[courseID].type === "C"}),
            instructors: this.props.instructors,
        });
    }

    resize() {
        let currentHideNav = (window.innerWidth <= 760);
        if (currentHideNav !== this.state.mobileView) {
            this.setState({ mobileView: !this.state.mobileView });
        }
    }

    goToRoute(route){
        this.props.history.push(this.props.match.url+route);
    }

    handleFilterClick(event) {
        event.preventDefault();
        this.setState({anchorEl:event.currentTarget});
    }

    handleClose(event) {
        event.preventDefault();
        this.setState({anchorEl:null});
    }

    handleFilterChange(filters, filterType){
        this.setState((state)=>{
            state.courses = filters && filters.length !== 0 ? Object.keys(this.props.courses).filter((courseID)=>{
                let course = this.props.courses[courseID];
                let reducedFilter;
               switch(filterType){
                   case 'instructor':
                       reducedFilter = filters.map((instructorField)=>{ return instructorField.value });
                       return reducedFilter.includes(course.instructor_id);
                   case 'grade':
                       reducedFilter = filters.map((gradeField)=>{ return Number(gradeField.value) });
                       return reducedFilter.includes(course.grade);
                   default:
                       return true;
               }
            }) : Object.keys(this.props.courses);
            state.filter[filterType] = filters ? filters : [];
            return state;
        });
    }

    renderFilter(filter){
        let options = [];
        switch(filter){
            case 'instructor':
                options = Object.keys(this.state.instructors).map((instructorID)=>{
                    let instructor = this.props.instructors[instructorID];
                    return {label: instructor.name, value: Number(instructorID)};
                });
                break;
            case 'subject':
                options = [{label:"math", value:"math"},{label:"science", value:"science"}, {label:"sat", value:"sat"}];
                break;
            case 'grade':
                options = [{label:"1", value:1},{label:"2", value:2}, {label:"3", value:3},
                    {label:"4", value:4},{label:"5", value:5}, {label:"6", value:6},
                    {label:"7", value:7},{label:"8", value:8}, {label:"9", value:9},
                    {label:"10", value:10},{label:"11", value:11}, {label:"12", value:12},
                ];
                break;
            default:
                return '';
        }
        options.push('All');
        const CustomClearText = () => 'clear all';
        const ClearIndicator = props => {
            const {
                children = <CustomClearText />,
                getStyles,
                innerProps: { ref, ...restInnerProps },
            } = props;
            return (
                <div
                    {...restInnerProps}
                    ref={ref}
                    style={getStyles('clearIndicator', props)}
                >
                    <div style={{ padding: '0px 5px' }}>{children}</div>
                </div>
            );
        };

        const ClearIndicatorStyles = (base, state) => ({
            ...base,
            cursor: 'pointer',
            color: state.isFocused ? 'blue' : 'black',
        });
        return <SearchSelect
            value={this.state.filter[filter]}
            onChange={(e)=>{this.handleFilterChange(e,filter)}}
            className={'filter-options'}
            closeMenuOnSelect={false}
            components={{ ClearIndicator }}
            placeholder={`All ${filter}s`}
            styles={{ clearIndicator: ClearIndicatorStyles }}
            isMulti
            options={options}
        />
    }

    render() {
        const open = Boolean(this.state.anchorEl);
        const id = open ? 'simple-popover' : undefined;
        const weekday = {
            0:"Sunday",
            1:"Monday",
            2:"Tuesday",
            3:"Wednesday",
            4:"Thursday",
            5:"Friday",
            6:"Saturday",
        };

        return (
            <Grid container>
                <Grid item xs={12}>
                    <Paper className="RegistrationLanding paper">
                        <BackButton/>
                        <hr/>
                        <Grid container alignItems={'center'} layout={'row'}>
                            <Grid item md={10} xs={10}>
                                <Typography variant={'h3'} align={'left'} className={"heading"}>Registration Catalog</Typography>
                            </Grid>
                            <Grid item md={2} xs={2}>
                                <FilterIcon
                                    style={{cursor:"pointer"}}
                                    onClick={(e)=>{ this.handleFilterClick.bind(this)(e) }}
                                    />
                            </Grid>
                        </Grid>
                        <div className={'registration-table'}>
                            {
                                this.state.courses.map((courseID)=>{
                                    let course = this.props.courses[courseID],
                                        start_date = new Date(course.schedule.start_date),
                                        end_date = new Date(course.schedule.end_date),
                                        start_time = course.schedule.start_time.substr(1),
                                        end_time = course.schedule.end_time.substr(1),
                                        days = course.schedule.days.map((day)=>{return weekday[day].substr(0,3) });
                                    start_date = start_date.toDateString().substr(3);
                                    end_date = end_date.toDateString().substr(3);
                                    let date = start_date + " - " + end_date,
                                        time = start_time + " - " + end_time;

                                    return (<Paper className={'row'} key={courseID}>
                                        <Grid container alignItems={'center'} layout={'row'}>
                                            <Grid item md={3}
                                                  onClick={(e) => {e.preventDefault(); this.goToRoute('/course/' + course.course_id + "/" + course.title)}}
                                                  style={{textDecoration: 'none', cursor: 'pointer'}}>
                                                <Typography className={'course-heading'} align={'left'}>
                                                    {course.title}
                                                </Typography>
                                            </Grid>
                                            <Grid item md={5}
                                                  onClick={(e) => {e.preventDefault(); this.goToRoute('/course/' + course.course_id + "/" + course.title)}}
                                                  style={{textDecoration: 'none', cursor: 'pointer'}}>
                                                <Grid container className={'course-detail'}>
                                                    <Grid item md={4} className={'heading-det'} align={'left'}>
                                                        Date
                                                    </Grid>
                                                    <Grid item md={8} className={'value'} align={'left'}>
                                                        {date} | {days} {time}
                                                    </Grid>
                                                </Grid>
                                                <Grid container className={'course-detail'}>
                                                    <Grid item md={4} className={'heading-det'} align={'left'}>
                                                        Instructor
                                                    </Grid>
                                                    <Grid item md={8}
                                                          className={'value'}
                                                          align={'left'}>
                                                        {this.state.instructors[course.instructor_id].name}
                                                    </Grid>
                                                </Grid>
                                                <Grid container className={'course-detail'}>
                                                    <Grid item md={4} className={'heading-det'} align={'left'}>
                                                        Tuition
                                                    </Grid>
                                                    <Grid item md={8} className={'value'} align={'left'}>
                                                        ${course.tuition}
                                                    </Grid>
                                                </Grid>
                                            </Grid>
                                        </Grid>
                                    </Paper>);
                                })
                            }
                        </div>
                    </Paper>
                </Grid>
                <Popover
                    id={id}
                    open={open}
                    anchorEl={this.state.anchorEl}
                    onClose={(e)=>{this.handleClose.bind(this)(e)}}
                    className={'registration-filter'}
                    anchorOrigin={{
                        vertical: 'bottom',
                        horizontal: 'right',
                    }}
                    transformOrigin={{
                        vertical: 'top',
                        horizontal: 'center',
                    }}
                >
                    {this.renderFilter('instructor')}
                    {this.renderFilter('subject')}
                    {this.renderFilter('grade')}
                </Popover>
            </Grid>
        )
    }
}

RegistrationLanding.propTypes = {
    stuffActions: PropTypes.object,
    RegistrationForms: PropTypes.array
};


export default withRouter(RegistrationLanding);
